import React from 'react';
import { useLocation } from 'react-router-dom';
import Footer from './Footer';

const ConditionalLayout = ({ children }) => {
  const location = useLocation();
  
  // Routes that should use minimalist layout (no regular footer)
  const minimalistRoutes = [
    '/ai-proficiency-challenge',
    '/coming-soon'
  ];
  
  // Check if current route should use minimalist layout
  const isMinimalistRoute = minimalistRoutes.some(route => 
    location.pathname.startsWith(route)
  );
  
  return (
    <>
      {children}
      {/* Only render regular footer for non-minimalist routes */}
      {!isMinimalistRoute && <Footer />}
    </>
  );
};

export default ConditionalLayout;
