              }

              .subtotal-amount {
                color: var(--accent-color);
                font-size: 1.1rem;
              }

              .total-amount {
                color: #059652;
                font-size: 1.2rem;
                font-weight: 700;
              }

              .payment-amount {
                color: #e3a127;
                font-size: 1.1rem;
              }

              /* Summary Card Styles */
              .breakdown-summary {
                background: linear-gradient(135deg, rgba(227, 161, 39, 0.1) 0%, rgba(227, 161, 39, 0.05) 100%);
