import React, { useState } from 'react';
import { useLanguage } from '../contexts/LanguageContext';
import { trackButtonClick } from '../utils/analytics';

const LanguageSelector = ({ className = '', style = {} }) => {
  const { language, switchLanguage } = useLanguage();
  const [isOpen, setIsOpen] = useState(false);

  const languages = [
    { code: 'es', name: 'EspaÃ±ol', flag: 'ðŸ‡ªðŸ‡¸' },
    { code: 'en', name: 'English', flag: 'ðŸ‡ºðŸ‡¸' }
  ];

  const currentLanguage = languages.find(lang => lang.code === language);

  const handleLanguageChange = (newLanguage) => {
    switchLanguage(newLanguage);
    setIsOpen(false);
    trackButtonClick(`Language Switch - ${newLanguage}`, 'Language Selector');
  };

  return (
    <div className={`language-selector ${className}`} style={style}>
      <div 
        className="language-current"
        onClick={() => setIsOpen(!isOpen)}
        style={{
          cursor: 'pointer',
          display: 'flex',
          alignItems: 'center',
          gap: '8px',
          padding: '8px 12px',
          border: '1px solid #ddd',
          borderRadius: '6px',
          backgroundColor: '#fff',
          fontSize: '14px',
          minWidth: '100px',
          position: 'relative'
        }}
      >
        <span>{currentLanguage?.flag}</span>
        <span>{currentLanguage?.code.toUpperCase()}</span>
        <i 
          className={`bi bi-chevron-${isOpen ? 'up' : 'down'}`}
          style={{ fontSize: '12px', marginLeft: 'auto' }}
        ></i>
      </div>
      
      {isOpen && (
        <div 
          className="language-dropdown"
          style={{
            position: 'absolute',
            top: '100%',
            left: '0',
            right: '0',
            backgroundColor: '#fff',
            border: '1px solid #ddd',
            borderRadius: '6px',
            marginTop: '4px',
            boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
            zIndex: 1000,
            overflow: 'hidden'
          }}
        >
          {languages.map((lang) => (
            <div
              key={lang.code}
              className="language-option"
              onClick={() => handleLanguageChange(lang.code)}
              style={{
                padding: '8px 12px',
                cursor: 'pointer',
                display: 'flex',
                alignItems: 'center',
                gap: '8px',
                fontSize: '14px',
                backgroundColor: language === lang.code ? '#f8f9fa' : '#fff',
                transition: 'background-color 0.2s'
              }}
              onMouseEnter={(e) => {
                if (language !== lang.code) {
                  e.target.style.backgroundColor = '#f1f3f4';
                }
              }}
              onMouseLeave={(e) => {
                if (language !== lang.code) {
                  e.target.style.backgroundColor = '#fff';
                }
              }}
            >
              <span>{lang.flag}</span>
              <span>{lang.name}</span>
              {language === lang.code && (
                <i 
                  className="bi bi-check"
                  style={{ marginLeft: 'auto', color: '#28a745', fontSize: '12px' }}
                ></i>
              )}
            </div>
          ))}
        </div>
      )}
      
      {/* Custom styles */}
      <style jsx>{`
        .language-selector {
          position: relative;
          display: inline-block;
        }
        
        .language-current:hover {
          border-color: #007bff !important;
        }
        
        @media (max-width: 768px) {
          .language-selector {
            position: static;
          }
          
          .language-dropdown {
            position: fixed !important;
            top: auto !important;
            bottom: 60px !important;
            left: 20px !important;
            right: 20px !important;
            width: auto !important;
          }
        }
      `}</style>
    </div>
  );
};

export default LanguageSelector;
