          color: var(--success);
          margin-bottom: 1rem;
        }
        
        a {
          color: var(--accent-color);
          text-decoration: none;
          transition: 0.3s;
        }
        
        a:hover {
          color: color-mix(in srgb, var(--accent-color), transparent 25%);
        }
        
        @media (max-width: 768px) {
          .container {
            padding: 1rem;
          }
          
          .game-card {
            padding: 1.5rem;
          }
          
          h1 {
            font-size: 2rem;
          }
          
          .score {
            font-size: 2.5rem;
          }
          
          .question-content {
            grid-template-columns: 1fr;
            gap: 1.5rem;
          }
          
          .question-image {
            order: 1;
          }
          
          .question-text-options {
            order: 2;
          }
          
          .question-img {
