        jsx: true,
        children: `
        /* Inherit edunest.co color scheme and design system */
        :root {
          --background-color: #031119;
          --default-color: rgba(255, 255, 255, 0.8);
          --heading-color: #e0e9f2;
          --accent-color: #e3a127;
          --surface-color: #1b262c;
          --contrast-color: #ffffff;
          --success: #059652;
          --warning: #ffc107;
          --danger: #df1529;
          --default-font: "Roboto", system-ui, -apple-system, "Segoe UI", Roboto, "Helvetica Neue", Arial, sans-serif;
          --heading-font: "Nunito Sans", sans-serif;
        }

        .back-navigation {
          margin-bottom: 2rem;
        }

        .back-arrow-btn {
          display: inline-flex;
          align-items: center;
          gap: 0.5rem;
          padding: 0.75rem 1.25rem;
          background: transparent;
          border: 2px solid rgba(255, 255, 255, 0.2);
          border-radius: 8px;
          color: var(--default-color);
          font-size: 0.9rem;
          font-weight: 500;
          cursor: pointer;
          transition: all 0.3s ease;
          text-decoration: none;
        }

        .back-arrow-btn:hover {
          border-color: var(--accent-color);
          background-color: rgba(227, 161, 39, 0.1);
          color: var(--accent-color);
          transform: translateX(-2px);
        }

        .back-arrow-btn i {
          font-size: 1.1rem;
        }



        .game-card {
          background: var(--surface-color);
          border: 1px solid rgba(255, 255, 255, 0.1);
          border-radius: 15px;
          box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
          padding: 2rem;
          margin-bottom: 2rem;
          transform: translateY(0);
          transition: transform 0.3s ease, box-shadow 0.3s ease;
        }

        .game-card:hover {
          transform: translateY(-5px);
          box-shadow: 0 15px 35px rgba(0, 0, 0, 0.4);
        }



        h2 {
          color: var(--heading-color);
          font-family: var(--heading-font);
          margin-top: 0;
          font-weight: 600;
        }

        h3 {
          color: var(--heading-color);
          font-family: var(--heading-font);
          font-weight: 600;
        }

        p {
          color: var(--default-color);
          line-height: 1.6;
        }

        .progress-container {
          width: 100%;
          height: 10px;
          background-color: rgba(255, 255, 255, 0.1);
          border-radius: 5px;
          margin-bottom: 2rem;
        }

        .progress-bar {
          height: 100%;
          border-radius: 5px;
          background: var(--accent-color);
          width: 0%;
          transition: width 0.5s ease;
        }

        .question-content {
          display: grid;
          grid-template-columns: 1fr 1fr;
          gap: 2rem;
          margin-bottom: 1.5rem;
          align-items: start;
        }

        .question-image {
          display: flex;
          justify-content: center;
          align-items: flex-start;
        }

        .question-img {
          width: 100%;
          // max-width: 400px;
          height: auto;
          border-radius: 12px;
          box-shadow: 0 8px 25px rgba(0, 0, 0, 0.3);
          transition: transform 0.3s ease, box-shadow 0.3s ease;
        }

        .question-img:hover {
          transform: translateY(-5px);
          box-shadow: 0 12px 35px rgba(0, 0, 0, 0.4);
        }

        .question-text-options {
          display: flex;
          flex-direction: column;
        }

        .question {
          margin-bottom: 1.5rem;
          font-weight: 500;
          font-size: 1.1rem;
          color: var(--heading-color);
        }

        .options {
          display: grid;
          grid-template-columns: 1fr;
          gap: 1rem;
        }

        .option {
          padding: 1rem;
          border: 2px solid rgba(255, 255, 255, 0.1);
          border-radius: 8px;
          cursor: pointer;
          transition: all 0.3s ease;
          background-color: rgba(255, 255, 255, 0.02);
          color: var(--default-color);
        }

        .option:hover {
          border-color: var(--accent-color);
          background-color: rgba(227, 161, 39, 0.1);
          transform: translateY(-2px);
        }

        .option.selected {
          border-color: var(--accent-color);
          background-color: rgba(227, 161, 39, 0.15);
          color: var(--contrast-color);
        }

        .option.correct {
          border-color: var(--success);
          background-color: rgba(5, 150, 82, 0.15);
        }

        .option.incorrect {
          border-color: var(--danger);
          background-color: rgba(223, 21, 41, 0.15);
        }

        .btn {
          display: inline-block;
          padding: 0.75rem 1.5rem;
          border: none;
          border-radius: 50px;
          font-weight: 600;
          cursor: pointer;
          transition: all 0.3s ease;
          font-size: 0.9rem;
          letter-spacing: 1px;
          text-transform: uppercase;
        }

        .btn-primary {
          background-color: var(--accent-color);
          color: var(--contrast-color);
          border: 1px solid var(--accent-color);
        }

        .btn-primary:hover {
          transform: translateY(-2px);
