import React, { useState, useEffect } from 'react';
import { useTranslation } from '../hooks/useTranslation';
import { trackButtonClick } from '../utils/analytics';

const HeroBanner = () => {
  const { t } = useTranslation();
  const [currentSlide, setCurrentSlide] = useState(0);

  // Slide data with variations of SOLUCIONES ELEARNING content
  const slides = [
    {
      id: 0,
      mainHeading: t('hero.mainHeading'),
      description: t('hero.description'),
      highlight: "interactivas"
    },
    {
      id: 1,
      mainHeading: "SOLUCIONES ELEARNING",
      description: "Diseñamos contenido pedagógico con avatares 3D y plataformas ágiles que revolucionan la educación digital.",
      highlight: "revolucionan"
    },
    {
      id: 2,
      mainHeading: "SOLUCIONES ELEARNING", 
      description: "Creamos experiencias de aprendizaje inmersivas que incrementan el engagement estudiantil y mejoran los resultados académicos.",
      highlight: "inmersivas"
    }
  ];

  // Auto-rotate slides every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prev) => (prev + 1) % slides.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [slides.length]);

  const handleSlideChange = (index) => {
    setCurrentSlide(index);
  };

  const handlePrevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + slides.length) % slides.length);
  };

  const handleNextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  return (
    <div className="hero-banner">
      <div className="row align-items-center">
        {/* Content Column */}
        <div className="col-lg-6">
          <div className="content">
            <div className="agency-name">
              <h5>{t('hero.agency')}</h5>
            </div>

            <div className="banner-carousel">
              <div className="slides-wrapper">
                {slides.map((slide, index) => (
                  <div
                    key={slide.id}
                    className={`slide ${index === currentSlide ? 'active' : ''} ${
                      index < currentSlide ? 'prev' : index > currentSlide ? 'next' : ''
                    }`}
                    style={{
                      transform: `translateX(${(index - currentSlide) * 100}%)`,
                      opacity: index === currentSlide ? 1 : 0,
                    }}
                  >
                    <div className="main-heading">
                      <h1>{slide.mainHeading}</h1>
                    </div>

                    <div className="divider"></div>

                    <div className="description">
                      <p>
                        {slide.description.split(slide.highlight).map((part, i, arr) => (
                          <React.Fragment key={i}>
                            {part}
                            {i < arr.length - 1 && (
                              <span className="highlight-text">{slide.highlight}</span>
                            )}
                          </React.Fragment>
                        ))}
                      </p>
                    </div>
                  </div>
                ))}
              </div>

              {/* Navigation arrows */}
              <button
                className="carousel-control prev"
                onClick={handlePrevSlide}
                aria-label="Slide anterior"
              >
                <i className="bi bi-chevron-left"></i>
              </button>

              <button
                className="carousel-control next"
                onClick={handleNextSlide}
                aria-label="Siguiente slide"
              >
                <i className="bi bi-chevron-right"></i>
              </button>

              {/* Slide indicators */}
              <div className="slide-indicators">
                {slides.map((_, index) => (
                  <button
                    key={index}
                    className={`indicator ${index === currentSlide ? 'active' : ''}`}
                    onClick={() => handleSlideChange(index)}
                    aria-label={`Ir a slide ${index + 1}`}
                  />
                )
                )}
              </div>
            </div>

            <div className="cta-button">
              <a
                href="#services"
                className="btn"
                onClick={() => trackButtonClick('Hero CTA - Ver Servicios', 'Hero Banner')}
              >
                <span>{t('hero.ctaButton')}</span>
                <i className="bi bi-arrow-right"></i>
              </a>
            </div>
          </div>
        </div>

        {/* Media Column */}
        <div className="col-lg-6">
          <div className="media-content">
            {/* Placeholder for image or video */}
            <div className="media-placeholder">
              <div className="media-container">
                {/* You can replace this with an image or video */}
                <div className="placeholder-content">
                  <i className="bi bi-play-circle placeholder-icon"></i>
                  <p className="placeholder-text">Espacio para imagen o video</p>
                </div>
                
                {/* Example of how to add an image */}
                {/* <img src="/assets/img/hero-image.jpg" alt="eLearning Solutions" className="hero-media" /> */}
                
                {/* Example of how to add a video */}
                {/* <video className="hero-media" controls>
                  <source src="/assets/video/hero-video.mp4" type="video/mp4" />
                  Your browser does not support the video tag.
                </video> */}
                
                {/* Example of embedded video (like the original iframe) */}
                {/* <div className="video-embed">
                  <iframe
                    src="https://iframe.mediadelivery.net/embed/354319/33fc09af-d689-4669-8c79-82816f38f0a7?autoplay=true&loop=false&muted=false&preload=true&responsive=true"
                    loading="lazy"
                    allow="accelerometer;gyroscope;autoplay;encrypted-media;picture-in-picture;"
                    allowFullScreen={true}
                    title="Video ejemplo de 3D personajes"
                  ></iframe>
                </div> */}
              </div>
              
              {/* Optional stats card overlay */}
              <div className="stats-overlay">
                <div className="stats-number">
                  <h3>100+</h3>
                </div>
                <div className="stats-label">
                  <p>{t('hero.statsLabel')}</p>
                </div>
                <div className="stats-arrow">
                  <a href="#portafolio"><i className="bi bi-arrow-up-right"></i></a>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default HeroBanner;
