import React, { useEffect } from 'react';
import { useTranslation } from '../hooks/useTranslation';
import { trackButtonClick } from '../utils/analytics';

const PortfolioDetails = ({ 
  projectData = {
    title: "Track 3rd Party & Approvals",
    badge: "UX/UI Design",
    date: "September 2024",
    client: "DigitalCraft Solutions",
    website: "projectwebsite.example.com",
    websiteUrl: "#",
    description: "A UX Overhaul for Material Receipts",
    mainImages: [
      "assets/img/portfolio/placeholder-1.webp",
      "assets/img/portfolio/placeholder-2.webp",
      "assets/img/portfolio/placeholder-3.webp"
    ],
    thumbnailImages: [
      "assets/img/portfolio/placeholder-4.webp",
      "assets/img/portfolio/placeholder-5.webp",
      "assets/img/portfolio/placeholder-6.webp",
      "assets/img/portfolio/placeholder-7.webp",
      "assets/img/portfolio/placeholder-8.webp",
      "assets/img/portfolio/placeholder-9.webp"
    ],
    techStack: ["Angular", "Express.js", "PostgreSQL", "GraphQL", "Firebase"],
    accordion: [
      {
        id: "overview",
        title: "Project Overview",
        icon: "bi-clipboard-data",
        content: "Goals: Streamline MR workflows across Field Engineers → Providers → Finance while ensuring audit trails from Finance Team and compliance for internal Company teams. Finance needed better tools to validate MRs (approved/rejected) before payment.\n\nRole: UX Validator/Analyst (identified gaps via stakeholder notes and heuristic evaluation).",
        expanded: true
      },
      {
        id: "methodology",
        title: "Methodology",
        icon: "bi-exclamation-diamond",
        content: "Benchmarking: 5 competitor analyses informed patterns (e.g., status tracking).\n\nStakeholder Notes: Parsed conversations with Stakeholders: Business (Finance) & Product Owner to flag ambiguities, validate all requirements, api usages, previous experience with current app. Mapped actions per user type. Highlighted friction in provider/finance handoffs Collaboration: Bridged gaps between devs and Product Owner to fit time requirements, technology capabilities, best methodology, solve all questions that could have remained.\n\nHeuristic Review: Highlighted navigation issues (breadcrumbs), consistency (language), feedback loops (notifications) new Material Receipts hierachy requirements. MR for each Proyect, Well & Stage.",
        expanded: false
      },
      {
        id: "findings",
        title: "Findings & Key Improvements Proposed",
        icon: "bi-award",
        content: "Language: Mixed Spanish/English terms caused confusion (e.g., \"Proveedor\" vs. \"Provider\").\n\nNavigation: Missing \"Go back\" buttons disrupted multi-step workflows. Unexistent breadcrumbs to Well selection screens. Redesign status indicators (progress percentages avoided because of time dev constrains)\n\nUser Flow: Map MR lifecycle (creation → approval → closure) with notification touchpoints.",
        expanded: false
      },
      {
        id: "solution",
        title: "The Solution",
        icon: "bi-gear",
        content: "UI/UX Improvements:\n- Dashboard required to be Role-Specific\n- Clearer role-based UI paths (e.g., hyperlinked breadcrumbs for Wells)\n- Field Engineers: Quick MR PDF generation with pre-filled forms\n- Finance: MR validation panel with fast search options\n- Reduced manual errors in MR handoffs (Finance could trace MRs to Providers)\n- Confirmation Alerts: Notify Providers upon MR acceptance by Finance (mail whole team)\n\nBackend Logic:\n- Auto-remove Providers post-payment (flagged as a requirement)\n- Dual-signature enforcement in PDF MRs (automated by active user)",
        expanded: false
      },
      {
        id: "impact",
        title: "Why This Matters",
        icon: "bi-lightbulb",
        content: "Holistic View: Ties UI improvements (e.g., breadcrumbs) to user roles (e.g., Finance needs quick access).\n\nBusiness Impact: Shows how UX reduces workflow bottlenecks (e.g., payment delays).\n\nMantain UI Business related Brand and Lookn’Feel",
        expanded: false
      }
    ],
    features: [
      "Role-Specific Dashboard",
      "MR PDF Generation",
      "Validation Panel",
      "Confirmation Alerts",
      "Auto-remove Providers",
      "Dual-signature Enforcement"
    ],
    liveProjectUrl: "#",
    nextProjectUrl: "#"
  },
  onClose
}) => {
  const { t } = useTranslation();

  // Initialize Swiper when component mounts
  useEffect(() => {
    if (window.Swiper) {
      const swiper = new window.Swiper('.portfolio-details-slider', {
        loop: true,
        speed: 1000,
        autoplay: {
          delay: 6000
        },
        effect: 'creative',
        creativeEffect: {
          prev: {
            shadow: true,
            translate: [0, 0, -400]
          },
          next: {
            translate: ['100%', 0, 0]
          }
        },
        slidesPerView: 1,
        navigation: {
          nextEl: '.swiper-button-next',
          prevEl: '.swiper-button-prev'
        }
      });

      return () => {
        if (swiper) swiper.destroy();
      };
    }
  }, []);

  const handleFeatureClick = (feature) => {
    trackButtonClick(`Portfolio Feature: ${feature}`, 'Portfolio Details');
  };

  const handleAccordionClick = (accordionTitle) => {
    trackButtonClick(`Portfolio Accordion: ${accordionTitle}`, 'Portfolio Details');
  };

  return (
    <div className="portfolio-details-modal">
      <div className="modal-backdrop" onClick={onClose}></div>
      <div className="modal-content">
        <button className="modal-close" onClick={onClose}>
          <i className="bi bi-x-lg"></i>
        </button>
        
        <section className="portfolio-details section">
          <div className="container" data-aos="fade-up" data-aos-delay="100">
            <div className="row">
              <div className="col-12">
                <div className="portfolio-details-content">
                  {/* Project Meta */}
                  <div className="project-meta" data-aos="fade-up">
                    <div className="badge-wrapper">
                      <span className="project-badge">{projectData.badge}</span>
                    </div>
                    <div className="date-client">
                      <div className="meta-item">
                        <i className="bi bi-calendar-check"></i>
                        <span>{projectData.date}</span>
                      </div>
                      <div className="meta-item">
                        <i className="bi bi-buildings"></i>
                        <span>{projectData.client}</span>
                      </div>
                    </div>
                  </div>

                  {/* Project Title */}
                  <h2 className="project-title" data-aos="fade-up" data-aos-delay="100">{projectData.title}</h2>

                  {/* Project Website */}
                  <div className="project-website" data-aos="fade-up" data-aos-delay="150">
                    <i className="bi bi-link-45deg"></i>
                    <a href={projectData.websiteUrl} target="_blank" rel="noopener noreferrer">
                      {projectData.website}
                    </a>
                  </div>

                  {/* Project Description */}
                  <div className="project-overview" data-aos="fade-up" data-aos-delay="200">
                    <p className="lead">{projectData.description}</p>
                  </div>

                  {/* Video Iframe */}
                  <div className="portfolio-video" data-aos="fade-up" data-aos-delay="225">
                    <div style={{position:'relative', paddingTop:'56.25%'}}>
                      <iframe 
                        src="https://iframe.mediadelivery.net/embed/354319/fa7b0fe7-d5bb-447d-84ff-dd3c3387afaf?autoplay=true&loop=false&muted=false&preload=true&responsive=true" 
                        loading="lazy" 
                        style={{border:0, position:'absolute', top:0, height:'100%', width:'100%'}} 
                        allow="accelerometer;gyroscope;autoplay;encrypted-media;picture-in-picture;" 
                        allowFullScreen
                      ></iframe>
                    </div>
                  </div>

                  {/* Main Images Slider */}
                  <div className="portfolio-details-media" data-aos="fade-up" data-aos-delay="250">
                    <div className="main-image">
                      <div className="portfolio-details-slider swiper init-swiper">
                        <div className="swiper-wrapper">
                          {projectData.mainImages.map((image, index) => (
                            <div key={index} className="swiper-slide">
                              <img src={image} alt={`${projectData.title} Image ${index + 1}`} className="img-fluid" />
                            </div>
                          ))}
                        </div>
                        <div className="swiper-button-prev"></div>
                        <div className="swiper-button-next"></div>
                      </div>
                    </div>
                  </div>

                  {/* Thumbnail Grid - 6 images (3x2 layout) */}
                  <div className="thumbnail-grid" data-aos="fade-up" data-aos-delay="300">
                    <div className="row g-2 mt-3">
                      {projectData.thumbnailImages.map((image, index) => (
                        <div key={index} className="col-4">
                          <img src={image} alt={`Gallery Image ${index + 1}`} className="img-fluid glightbox" />
                        </div>
                      ))}
                    </div>
                  </div>

                  {/* Tech Stack */}
                  <div className="tech-stack-badges" data-aos="fade-up" data-aos-delay="350">
                    {projectData.techStack.map((tech, index) => (
                      <span key={index}>{tech}</span>
                    ))}
                  </div>

                  {/* Project Accordion */}
                  <div className="project-overview" data-aos="fade-up" data-aos-delay="400">
                    <div className="accordion project-accordion" id="portfolio-details-projectAccordion">
                      {projectData.accordion.map((item, index) => (
                        <div key={item.id} className="accordion-item">
                          <h2 className="accordion-header">
                            <button 
                              className={`accordion-button ${!item.expanded ? 'collapsed' : ''}`}
                              type="button" 
                              data-bs-toggle="collapse" 
                              data-bs-target={`#portfolio-details-collapse-${index + 1}`}
                              aria-expanded={item.expanded}
                              aria-controls={`portfolio-details-collapse-${index + 1}`}
                              onClick={() => handleAccordionClick(item.title)}
                            >
                              <i className={`bi ${item.icon} me-2`}></i> {item.title}
                            </button>
                          </h2>
                          <div 
                            id={`portfolio-details-collapse-${index + 1}`} 
                            className={`accordion-collapse collapse ${item.expanded ? 'show' : ''}`}
                            data-bs-parent="#portfolio-details-projectAccordion"
                          >
                            <div className="accordion-body">
                              <p style={{whiteSpace: "pre-line"}}>{item.content}</p>
                            </div>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>

                  {/* Key Features */}
                  <div className="project-features" data-aos="fade-up" data-aos-delay="450">
                    <h3><i className="bi bi-stars"></i> Key Features</h3>
                    <div className="row g-3">
                      <div className="col-md-6">
                        <ul className="feature-list">
                          {projectData.features.slice(0, 3).map((feature, index) => (
                            <li key={index} onClick={() => handleFeatureClick(feature)}>
                              <i className="bi bi-check2-circle"></i> {feature}
                            </li>
                          ))}
                        </ul>
                      </div>
                      <div className="col-md-6">
                        <ul className="feature-list">
                          {projectData.features.slice(3).map((feature, index) => (
                            <li key={index} onClick={() => handleFeatureClick(feature)}>
                              <i className="bi bi-check2-circle"></i> {feature}
                            </li>
                          ))}
                        </ul>
                      </div>
                    </div>
                  </div>

                  {/* CTA Buttons */}
                  <div className="cta-buttons" data-aos="fade-up" data-aos-delay="500">
                    <a 
                      href={projectData.liveProjectUrl} 
                      className="btn-view-project"
                      onClick={() => trackButtonClick('View Live Project', 'Portfolio Details')}
                    >
                      View Live Project
                    </a>
                    <a 
                      href={projectData.nextProjectUrl} 
                      className="btn-next-project"
                      onClick={() => trackButtonClick('Next Project', 'Portfolio Details')}
                    >
                      Next Project <i className="bi bi-arrow-right"></i>
                    </a>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </section>
      </div>
    </div>
  );
};

export default PortfolioDetails;
